import React, { useState, useEffect } from 'react'

// import Informations from './components/Informations';
// import { KTSVG } from '../../../_metronic/helpers'
import { Alert, Card, Image } from 'antd';

import { connect } from 'react-redux';

import { Row, Col } from 'reactstrap';

import baseUrl from '../../config/baseUrl';
import serverUrl from '../../config/serverUrl';
import axiosApi from '../../config/axiosConfig';
import sideNotification from '../../commons/sideNotification';

import ReactHtmlParser from 'react-html-parser';

const Information = (props) => {
	const [isModalVisible, setIsModalVisible] = useState(false);
	const [userRecord, setUserRecord] = useState(null);
	const [loading, setLoading] = useState({
		isSaveLoading: false
	})
	
	useEffect(() => {
		if (props.recordId !== "") {
			getById(props.recordId);
		}
    }, []);

	const getById = (id) => {
        axiosApi.get(`/application_user/getViewById/${id}`)
        .then(res => {
            var r = res.data;
			if (r.status) {
				setUserRecord(r.data);
			} else {
				sideNotification.open("Failed", r.message, r.status)
			}
        });
    }

	
	return (
		<React.Fragment>
			<div className="container mt-3 pr-0 pl-0">
				<div style={{backgroundColor: "#ccc", width: "100%", height: "180px"}}>

				</div>
				<Row className="">
					<Col md="12" lg="12" xl="12" className="mb-3">
						<Card className='mb-4 pb-0 borderShadow5'>
							<Image
								width={150}
								height={150}
								style={{borderRadius: "200px", marginTop: "-100px"}}
								preview={false}
								src={(userRecord ? serverUrl + "/" + userRecord.filepath : "")}
								fallback="data:image/png;base64,"
							/>

							<span className="fa fa-edit text-muted cursor-pointer position-absolute end-0 mr-2" style={{marginRight: "20px"}} onClick={() => setIsModalVisible(true)}></span>
							<div className='d-flex flex-wrap flex-sm-nowrap mb-2' style={{marginTop: "-90px"}}>
								<div className='flex-grow-1'>
									<div className='d-flex justify-content-between align-items-start flex-wrap mb-2'>
										<div className='d-flex flex-column'>
											<div className='d-flex align-items-center mb-2'>
												<span className='text-muted text-hover-primary f-20 fw-bolder me-1'>
													{userRecord ? userRecord.full_name : "-"} <img src={baseUrl + "/assets/icons/verified.svg"} />
												</span>
											</div>
											<div className='d-flex align-items-center mb-2 mt-n1'>
												<span className='text-muted text-hover-primary f-15 fw-bolder me-1'>
													{userRecord ? userRecord.title : "-"}
												</span>
											</div>
										</div>
									</div>
								</div>
							</div>
						</Card>
						<Card 
							title="Tentang Saya"
							className="borderShadow5">
							<div className='row g-5 g-xxl-8'>
								<div className='col-md-12'>
									{(() => {
										if (userRecord && userRecord.description !== "") {
											return (
												<>{ReactHtmlParser(userRecord.description)}</>
											)
										} else {
											return (
												<Alert message={<p className="text-center mb-0">No Information Available</p>} type="warning" />
											)
										}
									})()}
								</div>
							</div>
						</Card>
					</Col>
				</Row>
			</div>
		</React.Fragment>
	)
}

const mapStateToProps = (state) => {
    return {
        user: state.auth.user,
    }
}
export default connect(mapStateToProps)(Information);